package string;

public class Demo02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String s1 = "Priya";
		String s2 = "Priya";
		System.out.println(s1);
		System.out.println(s1.indexOf('i'));
		System.out.println(s1.contains("iya"));
		System.out.println(s1.contains("pryy"));
		System.out.println(s1.endsWith("pr"));
		System.out.println(s1.startsWith("ya"));
	}

}
